import 'package:flutter/material.dart';

import 'package:meu_projeto_integrador/models/item_model.dart';
import 'package:meu_projeto_integrador/widgets/item_list_card.dart';

class ItemListScreen extends StatelessWidget {
  const ItemListScreen({super.key});

  //Dados simulados para lista
  static final List<Item> _itens = [
    Item(
      id: 1,
      nome: 'La Violetera',
      preco: 4.49,
      imageUrl: 'data:image/jpeg;base64,',
      descricao: 'Sabor irresist√≠vel',
    ),
    Item(
      id: 2,
      nome: 'Oliveira',
      preco: 7.50,
      imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQxQVWpq-_rZzmJM5S28bz8IU5eRCmKcrpjQQ&s',
      descricao: 'Qualidade no Sabor',
    ),
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Lista de Itens'),
      ),
      body: ListView.builder(
        itemCount: _itens.length,
        itemBuilder: (context, index){
          final item = _itens[index];
          return ItemListCard(item: item, onTap: (){
            ScaffoldMessenger.of(context).showSnackBar(
              SnackBar(content: Text('Clicou no item: ${item.nome}'),
              ));
          });
        }
        )
    );
  }
}
